mapper.select:
select count(*) 
from 
tb_basic
where 1=1

	and name = #{name}


	and post = #{post}


	and applicationArea = #{applicationAreas}


	and sex = #{sex}


	and marriage = #{marriage}


	and created_date <= #{createEnd}


	and created_date >= #{createStart}


	and date_of_birth <= #{birthEnd}


	and date_of_birth >= #{birthStart}

select *
from tb_basic
where 1=1

	and name = #{name}


	and post = #{post}


	and applicationArea = #{applicationAreas}


	and sex = #{sex}


	and marriage = #{marriage}


	and created_date <= #{createEnd}


	and created_date >= #{createStart}


	and date_of_birth <= #{birthEnd}


	and date_of_birth >= #{birthStart}

mapper.update:
update tb_basic 	

last_modified_date=CURDATE(),

	status=#{status }


where  id = #{id }
